import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product, selectedSize } = location.state || {};

  const [paymentMethod, setPaymentMethod] = useState("");
  const [upiId, setUpiId] = useState("");

  if (!product || !selectedSize) {
    navigate("/");
    return null;
  }

  const handlePayment = () => {
    if (!paymentMethod) {
      alert("Please select a payment method.");
      return;
    }

    if (paymentMethod === "UPI" && !upiId) {
      alert("Please enter a UPI ID.");
      return;
    }

    alert("Payment Successful!");
    navigate("/");
  };

  // Example QR image using PhonePe/Paytm (replace with real if needed)
  const qrImage = `https://api.qrserver.com/v1/create-qr-code/?data=upi://pay?pa=7639019726@upi&pn=User&am=${product.price}&size=200x200`;

  return (
    <div className="container mt-5">
      <h2>Payment Page</h2>
      <div className="card p-4">
        <h4>{product.name}</h4>
        <p>Price: â‚¹{product.price}</p>
        <p>Selected Size: {selectedSize}</p>
        <hr />
        <p>ðŸ’³ Choose a payment method:</p>
        <select
          className="form-select mb-3"
          value={paymentMethod}
          onChange={(e) => setPaymentMethod(e.target.value)}
        >
          <option value="">-- Select Payment Method --</option>
          <option value="Card">Credit/Debit Card</option>
          <option value="UPI">UPI</option>
          <option value="COD">Cash on Delivery</option>
        </select>

        {paymentMethod === "UPI" && (
          <>
            <div className="mb-3">
              <label>Enter your UPI ID:</label>
              <input
                type="text"
                className="form-control"
                value={upiId}
                onChange={(e) => setUpiId(e.target.value)}
                placeholder="e.g., yourname@upi"
              />
            </div>
            <div className="mb-3">
              <label>Scan to Pay:</label>
              <br />
              <img src={qrImage} alt="UPI QR" />
              <p className="text-muted">Scan using any UPI app</p>
            </div>
          </>
        )}

        <button className="btn btn-success mt-3" onClick={handlePayment}>
          Pay Now
        </button>
      </div>
    </div>
  );
};

export default PaymentPage;
