import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product, selectedSize } = location.state || {};

  if (!product || !selectedSize) {
    navigate("/");
    return null;
  }

  const handlePayment = () => {
    // Add product + size to localStorage orders
    const orders = JSON.parse(localStorage.getItem("orders")) || [];
    orders.push({ ...product, selectedSize, orderDate: new Date().toISOString() });
    localStorage.setItem("orders", JSON.stringify(orders));

    // Redirect to orders page
    navigate("/account/orders");
  };

  return (
    <div className="container mt-5 text-center">
      <h2>Payment Page</h2>
      <img src={product.img} alt={product.name} style={{ maxHeight: "200px" }} />
      <h3>{product.name}</h3>
      <p>Price: {product.price}</p>
      <p>Size: <strong>{selectedSize}</strong></p>
      <button className="btn btn-primary mt-3" onClick={handlePayment}>
        Pay Now
      </button>
    </div>
  );
};

export default PaymentPage;
