import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Account = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("user"); // or your auth key
    navigate("/");
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="mb-3 shadow-sm" onClick={() => navigate("/account/profile")} style={{ cursor: "pointer" }}>
            <Card.Body>
              <h5>ðŸ‘¤ Profile</h5>
              <p>View and edit your personal information</p>
            </Card.Body>
          </Card>

          <Card className="mb-3 shadow-sm" onClick={() => navigate("/account/orders")} style={{ cursor: "pointer" }}>
            <Card.Body>
              <h5>ðŸ“¦ Orders</h5>
              <p>Track and manage your orders</p>
            </Card.Body>
          </Card>

          <Card className="shadow-sm" onClick={handleLogout} style={{ cursor: "pointer" }}>
            <Card.Body>
              <h5 className="text-danger">ðŸšª Logout</h5>
              <p>Sign out from your account</p>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Account;
