import React from "react";
import { useNavigate } from "react-router-dom"; // ✅ Import navigation hook
import '../App.css';

const products = [
  // ... (same as your existing product array)
];

const Products = () => {
  const navigate = useNavigate(); // ✅ Initialize the navigation hook

  const handleAddToCart = (product) => {
    // You can handle cart state here if needed
    navigate('/cart'); // ✅ Navigate to cart page
  };

  return (
    <div id="products">
      <header id="products-h" className="bg-dark text-white text-center p-5">
        <h1>Our Products</h1>
        <p>Your Destination for Affordable Fashion</p>
      </header>
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-3 col-sm-6 mb-4">
            <div className="card h-100">
              <img
                src={product.img}
                alt={product.name}
                className="card-img-top product-img m-3 img-hover"
              />
              <div className="card-body text-center">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">{product.price}</p>
                <div className="d-flex justify-content-between">
                  <button className="btn btn-grad">
                    Buy Now
                  </button>
                  <button
                    className="btn btn-add"
                    onClick={() => handleAddToCart(product)}
                    aria-label={`Add ${product.name} to Cart`}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
