import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ProductDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product } = location.state || {}; // Receive the product data passed via state

  // State to manage selected size and rating
  const [selectedSize, setSelectedSize] = useState("");
  const [rating, setRating] = useState(0);

  // If no product is passed, navigate back to the homepage
  useEffect(() => {
    if (!product) {
      navigate("/"); // Redirect if no product data exists
    }
  }, [product, navigate]);

  const handleSizeChange = (e) => {
    setSelectedSize(e.target.value); // Update the selected size
  };

  const handleRatingChange = (e) => {
    setRating(e.target.value); // Update the rating
  };

  const handleSubmit = () => {
    if (!selectedSize) {
      alert("Please select a size.");
      return;
    }

    if (rating < 1 || rating > 5) {
      alert("Rating must be between 1 and 5.");
      return;
    }

    alert(`Size: ${selectedSize}\nRating: ${rating}`);
    // You can handle the submission further, like adding to cart or saving to database
  };

  return (
    <div className="product-details">
      <header className="bg-dark text-white text-center p-5">
        <h1>{product?.name}</h1>
      </header>
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6">
            <img src={product?.img} alt={product?.name} className="img-fluid" />
          </div>
          <div className="col-md-6">
            <h3>Price: {product?.price}</h3>
            <p>
              <strong>Description:</strong> This is a detailed description of
              the {product?.name}. (Add the full description here.)
            </p>

            {/* Size selection */}
            <div>
              <label htmlFor="size">Select Size: </label>
              <select
                id="size"
                value={selectedSize}
                onChange={handleSizeChange}
              >
                <option value="">Choose a size</option>
                <option value="S">Small</option>
                <option value="M">Medium</option>
                <option value="L">Large</option>
                <option value="XL">Extra Large</option>
              </select>
            </div>

            {/* Rating input */}
            <div className="mt-3">
              <label htmlFor="rating">Rate this product: </label>
              <input
                type="number"
                id="rating"
                value={rating}
                onChange={handleRatingChange}
                min="1"
                max="5"
                placeholder="1-5"
              />
              <p>Rating: {rating}</p>
            </div>

            <button className="btn btn-grad mt-3" onClick={handleSubmit}>
              Submit Review
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
