import React from "react";
import { useCart } from "../context/CartContext";

const Cart = () => {
  const { cartItems } = useCart();

  return (
    <div className="container mt-4">
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Cart is empty.</p>
      ) : (
        cartItems.map((item) => (
          <div
            key={item.id}
            className="d-flex align-items-center mb-3 border-bottom pb-2"
          >
            <img
              src={item.image}
              alt={item.name}
              style={{ width: "80px", height: "80px", objectFit: "cover", marginRight: "15px", borderRadius: "10px" }}
            />
            <div>
              <h5>{item.name}</h5>
              <p className="mb-1">{item.price}</p>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Cart;
