import React, { useState, useEffect } from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";

const Products = ({ searchTerm }) => {
  const navigate = useNavigate();
  const { addToCart } = useCart();

  const [filteredProducts, setFilteredProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);

  // ✅ Fetch products from backend
  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        setAllProducts(data);
        setFilteredProducts(data);
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, []);

  // ✅ Filter logic
  useEffect(() => {
    if (searchTerm) {
      setFilteredProducts(
        allProducts.filter((product) =>
          product.name.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    } else {
      setFilteredProducts(allProducts);
    }
  }, [searchTerm, allProducts]);

  const handleAddToCart = (product) => {
    addToCart(product);
    navigate("/cart");
  };

  const handleBuyNow = (product) => {
    navigate("/product-details", { state: { product } });
  };

  return (
    <div id="products">
      <header className="bg-dark text-white text-center p-5">
        <h1>Our Products</h1>
        <p>Your Destination for Affordable Fashion</p>
      </header>
      <div className="row">
        {filteredProducts.length === 0 ? (
          <p>No products found</p>
        ) : (
          filteredProducts.map((product) => (
            <div key={product.id} className="col-md-3 col-sm-6 mb-4">
              <div className="card h-100">
                <img src={product.img} alt={product.name} className="card-img-top m-3" />
                <div className="card-body text-center">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">{product.price}</p>
                  <div className="d-flex justify-content-between">
                    <button className="btn btn-grad" onClick={() => handleBuyNow(product)}>
                      Buy Now
                    </button>
                    <button className="btn btn-add" onClick={() => handleAddToCart(product)}>
                      Add to Cart
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Products;
