import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ProductDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product } = location.state || {};

  // If no product is passed, redirect to the home page
  if (!product) {
    navigate("/");
    return null;
  }

  return (
    <div className="product-details">
      <header className="bg-dark text-white text-center p-5">
        <h1>{product.name}</h1>
      </header>
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6">
            <img src={product.img} alt={product.name} className="img-fluid" />
          </div>
          <div className="col-md-6">
            <h3>Price: {product.price}</h3>
            <p><strong>Description:</strong> This is a detailed description of the {product.name}. (Add the full description here.)</p>
            <p><strong>Available Sizes:</strong> S, M, L, XL</p>
            <button className="btn btn-grad">Buy Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
