import React from "react";
import "./App.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// Components
import Header from "./components/Header";
import Footer from "./components/Footer";
import HomePage from "./components/Offer";       // Home page component
import Products from "./components/Products";    // Products page component
import AboutPage from "./components/About";      // About page component
import ContactPage from "./components/Contact";  // Contact page component
import Welcome from "./components/Welcome";      // Welcome page component
import Cart from "./components/Cart";            // Cart page component
import Favorites from "./components/Favorites";  // Favorites page component

// Context Providers
import { CartProvider } from "./context/CartContext";
import { FavoriteProvider } from "./context/FavoriteContext";

function App() {
  return (
    <CartProvider>
      <FavoriteProvider>
        <Router>
          <Routes>
            {/* Main Route with all sections and header */}
            <Route
              path="/"
              element={
                <>
                  <Header />
                  <Welcome />
                  <HomePage />
                  <Products />
                  <AboutPage />
                  <ContactPage />
                </>
              }
            />

            {/* Cart page (can be without header if preferred) */}
            <Route
              path="/cart"
              element={
                <>
                  <Header />
                  <Cart />
                </>
              }
            />

            {/* Favorites page */}
            <Route
              path="/favorites"
              element={
                <>
                  <Header />
                  <Favorites />
                </>
              }
            />
          </Routes>

          {/* Footer shown on all pages */}
          <Footer />
        </Router>
      </FavoriteProvider>
    </CartProvider>
  );
}

export default App;
