import React, { useState } from "react";
import { Navbar, Nav, Container, Form, FormControl, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

const Header = () => {
  const [searchTerm, setSearchTerm] = useState(""); // Store the search term
  const [searchResults, setSearchResults] = useState([]); // Store the search results

  // Mock function to simulate a search. Replace it with real data fetching logic later.
  const handleSearch = (e) => {
    e.preventDefault(); // Prevent page reload

    // Log the current search term to check if it's working
    console.log("Searching for:", searchTerm);

    // Example of filtered mock results based on the search term
    const mockResults = [
      { id: 1, name: "T-shirt" },
      { id: 2, name: "Jeans" },
      { id: 3, name: "Shoes" },
    ];

    // Filter the results based on the search term
    const filteredResults = mockResults.filter((item) =>
      item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Set the filtered results to state
    setSearchResults(filteredResults);
  };

  return (
    <div>
      <Navbar bg="dark" variant="dark" expand="lg" sticky="top" className="custom-navbar">
        <Container>
          <Navbar.Brand as={Link} to="/">
            <img
              src="https://cdn-icons-png.freepik.com/256/12936/12936079.png"
              alt="Logo"
              style={{ width: "40px", height: "40px", borderRadius: "50%", marginRight: "10px" }}
            />
            FashionFlick
          </Navbar.Brand>

          <Navbar.Toggle aria-controls="navbar-nav" />
          <Navbar.Collapse id="navbar-nav">
            <Nav className="ms-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#products">Products</Nav.Link>
              <Nav.Link href="#about">About</Nav.Link>
              <Nav.Link href="#contact">Contact</Nav.Link>
              <Nav.Link as={Link} to="/cart">🛒 Cart</Nav.Link>

              <Form className="d-flex ms-3" onSubmit={handleSearch}>
                <FormControl
                  type="search"
                  placeholder="What are you looking for?"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)} // Update searchTerm on change
                  className="me-2 searchbox"
                  style={{ width: "250px", height: "35px" }}
                />
                <Button className="btn" type="submit" variant="outline-light" size="sm">
                  Search
                </Button>
              </Form>

              <Nav.Link as={Link} to="/profile">👤 Profile</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Display search results */}
      {searchResults.length > 0 ? (
        <div className="search-results">
          <h4>Search Results:</h4>
          <ul>
            {searchResults.map((result) => (
              <li key={result.id}>{result.name}</li>
            ))}
          </ul>
        </div>
      ) : (
        searchTerm && <p>No results found for "{searchTerm}"</p>
      )}
    </div>
  );
};

export default Header;
