import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product, selectedSize } = location.state || {};

  const handlePayment = () => {
    if (!product || !selectedSize) {
      alert("Invalid payment data.");
      return;
    }

    const order = {
      ...product,
      size: selectedSize,
      orderDate: new Date().toISOString(),
    };

    const existingOrders = JSON.parse(localStorage.getItem("orders")) || [];
    existingOrders.push(order);
    localStorage.setItem("orders", JSON.stringify(existingOrders));

    // Redirect to Orders Page
    navigate("/account/orders");
  };

  if (!product) return <p>Payment Error</p>;

  return (
    <div className="container mt-5 text-center">
      <h2>Confirm Payment</h2>
      <p>Product: <strong>{product.name}</strong></p>
      <p>Size: <strong>{selectedSize}</strong></p>
      <p>Price: <strong>{product.price}</strong></p>
      <button className="btn btn-success mt-3" onClick={handlePayment}>
        Pay Now
      </button>
    </div>
  );
};

export default PaymentPage;
