import React from "react";
import { useCart } from "../context/CartContext";
import { Link } from "react-router-dom"; // For optional 'view full product'

const Cart = () => {
  const { cartItems, removeFromCart } = useCart();

  return (
    <div className="container py-4">
      <h2 className="text-center mb-4">ðŸ›’ Your Cart</h2>

      {cartItems.length === 0 ? (
        <p className="text-center">Your cart is empty.</p>
      ) : (
        <div className="row">
          {cartItems.map((item) => (
            <div key={item.id} className="col-md-4 mb-4">
              <div className="card h-100 shadow-sm cart-img">
                <img
                  src={item.img}
                  alt={item.name}
                  className="card-img-top"
                  style={{ height: "200px", objectFit: "cover" }}
                />
                <div className="card-body text-center">
                  <h5 className="card-title">{item.name}</h5>
                  <p className="card-text">{item.price}</p>

                  {/* View full product placeholder */}
                  <Link to={`/product/${item.id}`} className="btn btn-outline-primary btn-sm mb-2">
                    View Full Product
                  </Link>

                  {/* Remove from cart button */}
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => removeFromCart(item.id)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
