import React from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useFavorites } from "./context/FavoriteContext"; // NEW

const products = [
  // (same product list as before)
];

const Products = () => {
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { addToFavorites } = useFavorites(); // NEW

  const handleAddToCart = (product) => {
    addToCart(product);
    navigate("/cart");
  };

  const handleAddToFavorites = (product) => {
    addToFavorites(product);
    navigate("/favorites");
  };

  return (
    <div id="products">
      <header id="products-h" className="bg-dark text-white text-center p-5">
        <h1>Our Products</h1>
        <p>Your Destination for Affordable Fashion</p>
      </header>
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-3 col-sm-6 mb-4">
            <div className="card h-100 position-relative">
              {/* Clickable Favorite Icon */}
              <span
                className="favorite-icon"
                title="Add to Favorites"
                onClick={() => handleAddToFavorites(product)}
                style={{
                  position: "absolute",
                  top: "10px",
                  right: "10px",
                  cursor: "pointer",
                  fontSize: "20px",
                  color: "red",
                }}
              >
                &#9829;
              </span>

              <img
                src={product.img}
                alt={product.name}
                className="card-img-top product-img m-3 img-hover"
              />
              <div className="card-body text-center">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">{product.price}</p>
                <div className="d-flex justify-content-between">
                  <button className="btn btn-grad">Buy Now</button>
                  <button
                    className="btn btn-add"
                    aria-label={`Add ${product.name} to Cart`}
                    onClick={() => handleAddToCart(product)}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
