import React, { useEffect, useState } from "react";

const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const storedOrders = JSON.parse(localStorage.getItem("orders")) || [];
    setOrders(storedOrders);
  }, []);

  return (
    <div className="container mt-5">
      <h2>Your Orders</h2>
      {orders.length === 0 ? (
        <p>No orders placed yet.</p>
      ) : (
        orders.map((order) => (
          <div key={order.orderId} className="card mb-3 shadow-sm">
            <div className="row g-0">
              <div className="col-md-4">
                <img src={order.img} alt={order.name} className="img-fluid rounded-start" />
              </div>
              <div className="col-md-8">
                <div className="card-body">
                  <h5 className="card-title">{order.name}</h5>
                  <p className="card-text"><strong>Price:</strong> {order.price}</p>
                  <p className="card-text"><strong>Size:</strong> {order.size}</p>
                  <p className="card-text"><small className="text-muted">Ordered on: {order.orderDate}</small></p>
                </div>
              </div>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Orders;
