import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ProductDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product } = location.state || {}; // Receive the product data passed via state

  // If no product is passed, navigate back to the homepage
  if (!product) {
    navigate("/");
    return null;
  }

  // Function to generate the stars based on rating
  const renderStars = (rating) => {
    const fullStars = Math.floor(rating); // Full gold stars
    const halfStar = rating % 1 !== 0; // Half-star condition
    const stars = [];

    // Add full stars
    for (let i = 0; i < fullStars; i++) {
      stars.push("ðŸŒŸ");
    }

    // Add half star if any
    if (halfStar) {
      stars.push("â­");
    }

    // Fill the remaining stars to make a total of 5
    while (stars.length < 5) {
      stars.push("â˜†"); // Empty stars
    }

    return stars.join(" ");
  };

  const handlePlaceOrder = () => {
    // Handle the logic to place the order (for example, navigate to a checkout page)
    navigate("/checkout", { state: { product } }); // Navigate to the checkout page with product details
  };

  return (
    <div className="product-details">
      <header className="bg-dark text-white text-center p-5">
        <h1>{product.name}</h1>
      </header>
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6">
            <img src={product.img} alt={product.name} className="img-fluid" />
          </div>
          <div className="col-md-6">
            <h3>Price: {product.price}</h3>
            <p>
              <strong>Description:</strong> This is a detailed description of
              the {product.name}. (Add the full description here.)
            </p>

            {/* Rating Display with Stars */}
            <div className="mt-3">
              <strong>Rating: </strong>
              <span style={{ fontSize: "24px" }}>
                {renderStars(product.rating || 0)} {/* Display stars */}
              </span>
            </div>

            {/* Place Order Button */}
            <div className="mt-4">
              <button className="btn btn-grad" onClick={handlePlaceOrder}>
                Place Order
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
