import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product, selectedSize } = location.state || {};

  const [paymentMethod, setPaymentMethod] = useState("");
  const [upiId, setUpiId] = useState("");
  const [cardDetails, setCardDetails] = useState({
    number: "",
    expiry: "",
    cvv: ""
  });

  const [doorNo, setDoorNo] = useState("");
  const [city, setCity] = useState("");
  const [pincode, setPincode] = useState("");
  const [phone, setPhone] = useState("");

  const [errors, setErrors] = useState({});

  if (!product || !selectedSize) {
    navigate("/");
    return null;
  }

  const validate = () => {
    const newErrors = {};
    if (!doorNo.trim()) newErrors.doorNo = "Door number is required.";
    if (!city.trim()) newErrors.city = "City is required.";
    if (!pincode.trim()) newErrors.pincode = "Pincode is required.";
    if (!phone.trim()) newErrors.phone = "Phone number is required.";
    if (!paymentMethod) newErrors.paymentMethod = "Select a payment method.";

    if (paymentMethod === "UPI" && !upiId.trim()) {
      newErrors.upiId = "UPI ID is required.";
    }

    if (paymentMethod === "Card") {
      const { number, expiry, cvv } = cardDetails;
      if (!number.trim()) newErrors.cardNumber = "Card number is required.";
      if (!expiry.trim()) newErrors.expiry = "Expiry date is required.";
      if (!cvv.trim()) newErrors.cvv = "CVV is required.";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handlePayment = () => {
    if (!validate()) return;

    const orders = JSON.parse(localStorage.getItem("orders")) || [];
    const newOrder = {
      ...product,
      selectedSize,
      address: { doorNo, city, pincode, phone },
      paymentMethod,
      date: new Date().toISOString()
    };
    orders.push(newOrder);
    localStorage.setItem("orders", JSON.stringify(orders));

    navigate("/account/orders");
  };

  const qrImage = `https://api.qrserver.com/v1/create-qr-code/?data=upi://pay?pa=7639019726@upi&pn=User&am=${product.price}&size=200x200`;

  return (
    <div className="container mt-5">
      <h2>Payment Page</h2>
      <div className="card p-4">
        <h4>{product.name}</h4>
        <p>Price: {product.price}</p>
        <p>Selected Size: {selectedSize}</p>

        <hr />
        <h5>üè† Delivery Address</h5>
        <div className="mb-3">
          <label>Door No. / Street:</label>
          <input
            type="text"
            className="form-control mb-1"
            value={doorNo}
            onChange={(e) => setDoorNo(e.target.value)}
            placeholder="e.g., 12/34 Main Street"
          />
          {errors.doorNo && <div className="text-danger">{errors.doorNo}</div>}

          <label>City:</label>
          <input
            type="text"
            className="form-control mb-1"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city"
          />
          {errors.city && <div className="text-danger">{errors.city}</div>}

          <label>Pincode:</label>
          <input
            type="text"
            className="form-control mb-1"
            maxLength="6"
            value={pincode}
            onChange={(e) => setPincode(e.target.value)}
            placeholder="e.g., 641001"
          />
          {errors.pincode && <div className="text-danger">{errors.pincode}</div>}

          <label>Phone Number:</label>
          <input
            type="text"
            className="form-control mb-1"
            maxLength="10"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            placeholder="e.g., 9876543210"
          />
          {errors.phone && <div className="text-danger">{errors.phone}</div>}
        </div>

        <hr />
        <p>üí≥ Choose a payment method:</p>
        <select
          className="form-select mb-2"
          value={paymentMethod}
          onChange={(e) => setPaymentMethod(e.target.value)}
        >
          <option value="">-- Select Payment Method --</option>
          <option value="Card">Credit/Debit Card</option>
          <option value="UPI">UPI</option>
          <option value="COD">Cash on Delivery</option>
        </select>
        {errors.paymentMethod && <div className="text-danger">{errors.paymentMethod}</div>}

        {paymentMethod === "Card" && (
          <div className="mb-3">
            <label>Card Number:</label>
            <input
              type="text"
              className="form-control mb-1"
              maxLength="16"
              value={cardDetails.number}
              placeholder="Enter 16-digit card number"
              onChange={(e) =>
                setCardDetails({ ...cardDetails, number: e.target.value })
              }
            />
            {errors.cardNumber && <div className="text-danger">{errors.cardNumber}</div>}

            <label>Expiry Date:</label>
            <input
              type="month"
              className="form-control mb-1"
              value={cardDetails.expiry}
              onChange={(e) =>
                setCardDetails({ ...cardDetails, expiry: e.target.value })
              }
            />
            {errors.expiry && <div className="text-danger">{errors.expiry}</div>}

            <label>CVV:</label>
            <input
              type="password"
              className="form-control"
              maxLength="3"
              placeholder="Enter CVV"
              value={cardDetails.cvv}
              onChange={(e) =>
                setCardDetails({ ...cardDetails, cvv: e.target.value })
              }
            />
            {errors.cvv && <div className="text-danger">{errors.cvv}</div>}
          </div>
        )}

        {paymentMethod === "UPI" && (
          <>
            <div className="mb-3">
              <label>UPI ID:</label>
              <input
                type="text"
                className="form-control"
                placeholder="e.g., yourname@upi"
                value={upiId}
                onChange={(e) => setUpiId(e.target.value)}
              />
              {errors.upiId && <div className="text-danger">{errors.upiId}</div>}
            </div>
            <div className="mb-3">
              <label>Scan to Pay:</label>
              <br />
              <img src={qrImage} alt="UPI QR" />
              <p className="text-muted">Scan with any UPI app</p>
            </div>
          </>
        )}

        <button className="btn btn-success mt-3" onClick={handlePayment}>
          Pay Now
        </button>
      </div>
    </div>
  );
};

export default PaymentPage;
