import { useFavourites } from "../context/FavouritesContext";

const Products = () => {
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { addToFavourites } = useFavourites(); // Access Favourites context

  const handleAddToCart = (product) => {
    addToCart(product);
    navigate("/cart");
  };

  const handleAddToFavourites = (product) => {
    addToFavourites(product);
    navigate("/favourites"); // Redirect to Favourites page
  };

  return (
    <div id="products">
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-3 col-sm-6 mb-4">
            <div className="card h-100 position-relative">
              <span
                className="favorite-icon"
                onClick={() => handleAddToFavourites(product)}
                style={{ cursor: "pointer", position: "absolute", top: 10, right: 10 }}
              >
                ❤️
              </span>
              <img src={product.img} alt={product.name} className="card-img-top product-img m-3 img-hover" />
              <div className="card-body text-center">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">{product.price}</p>
                <div className="d-flex justify-content-between">
                  <button className="btn btn-grad">Buy Now</button>
                  <button className="btn btn-add" onClick={() => handleAddToCart(product)}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
