import React, { useState, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import HomePage from "./components/Offer";
import Products from "./components/Products";
import AboutPage from "./components/About";
import ContactPage from "./components/Contact";
import Welcome from "./components/Welcome";
import Cart from "./components/Cart";
import ProductDetails from "./components/ProductDetails";
import { CartProvider } from "./context/CartContext";
import PaymentPage from "./components/PaymentPage";
import Account from "./components/Account";
import Orders from "./components/Orders";

function AppWrapper() {
  // We wrap Router and useLocation here to conditionally render Header
  const location = useLocation();
  const [searchTerm, setSearchTerm] = useState("");
  const productRef = useRef(null);

  // Hide header on /account and its subroutes
  const hideHeader = location.pathname.startsWith("/account");

  return (
    <CartProvider>
      {!hideHeader && (
        <Header
          setSearchTerm={setSearchTerm}
          scrollToProducts={() => {
            productRef.current?.scrollIntoView({ behavior: "smooth" });
          }}
        />
      )}
      <Routes>
        <Route
          path="/"
          element={
            <>
              <Welcome />
              <HomePage />
              <Products searchTerm={searchTerm} ref={productRef} />
              <AboutPage />
              <ContactPage />
            </>
          }
        />
        <Route path="/cart" element={<Cart />} />
        <Route path="/product-details" element={<ProductDetails />} />
        <Route path="/checkout" element={<PaymentPage />} />
        <Route path="/account" element={<Account />} />
        <Route path="/account/orders" element={<Orders />} />
      </Routes>
      <Footer />
    </CartProvider>
  );
}

function App() {
  return (
    <Router>
      <AppWrapper />
    </Router>
  );
}

export default App;
