import React, { useState } from "react";
import { Navbar, Nav, Container, Form, FormControl, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Products from "./Products";  // Import Products

const Header = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [expanded, setExpanded] = useState(false); // Track Navbar collapse state

  const handleSearch = (e) => {
    e.preventDefault();
    console.log("Searching for:", searchTerm);

    // Scroll down to the products section after search
    const productsSection = document.getElementById("products");
    if (productsSection) {
      productsSection.scrollIntoView({ behavior: "smooth" });
    }

    // Keep the Navbar expanded after search
    setExpanded(false);
  };

  return (
    <>
      <Navbar
        bg="dark"
        variant="dark"
        expand="lg"
        sticky="top"
        className="custom-navbar"
        expanded={expanded} // Control Navbar collapse state
      >
        <Container>
          <Navbar.Brand as={Link} to="/">
            <img
              src="https://cdn-icons-png.freepik.com/256/12936/12936079.png"
              alt="Logo"
              style={{ width: "40px", height: "40px", borderRadius: "50%", marginRight: "10px" }}
            />
            FashionFlick
          </Navbar.Brand>

          <Navbar.Toggle
            aria-controls="navbar-nav"
            onClick={() => setExpanded(!expanded)} // Toggle collapse state
          />
          <Navbar.Collapse id="navbar-nav">
            <Nav className="ms-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#products">Products</Nav.Link>
              <Nav.Link href="#about">About</Nav.Link>
              <Nav.Link href="#contact">Contact</Nav.Link>
              <Nav.Link as={Link} to="/cart">ðŸ›’ Cart</Nav.Link>

              <Form className="d-flex ms-3" onSubmit={handleSearch}>
                <FormControl
                  type="search"
                  placeholder="What are you looking for?"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="me-2 searchbox"
                  style={{ width: "250px", height: "35px" }}
                />
                <Button className="btn" type="submit" variant="outline-light" size="sm">
                  Search
                </Button>
              </Form>
              <Nav.Link as={Link} to="/profile">ðŸ‘¤ Profile</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Products searchTerm={searchTerm} />
    </>
  );
};

export default Header;
