// Cart.js
import React from "react";
import { useCart } from "../context/CartContext";

const Cart = () => {
  const { cartItems } = useCart();

  return (
    <div className="container">
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Cart is empty.</p>
      ) : (
        cartItems.map(item => (
          <div key={item.id}>
            {item.name} - {item.price}
          </div>
        ))
      )}
    </div>
  );
};

export default Cart;
