import React, { useState } from "react";
import { Navbar, Nav, Container, Form, FormControl, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import "../App.css";

const Header = () => {
  const [activeLink, setActiveLink] = useState("home");
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate(); // Initialize navigate

  const handleLinkClick = (link) => {
    if (link === "cart") {
      navigate("/cart"); // Redirect to cart page
    } else {
      setActiveLink(link);
      const targetElement = document.getElementById(link);
      if (targetElement) {
        targetElement.scrollIntoView({ behavior: "smooth" });
      }
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    console.log("Searching for:", searchTerm);
    // Add real search logic here later
  };

  return (
    <Navbar className="custom-navbar" variant="dark" expand="lg" sticky="top">
      <Container>
        <Navbar.Brand href="#" className="d-flex align-items-center">
          <img
            src="https://cdn-icons-png.freepik.com/256/12936/12936079.png?ga=GA1.1.1204324961.1740241601&semt=ais_hybrid"
            alt="FashionFlick Logo"
            className="project-image p-1"
            style={{ width: "40px", height: "40px", borderRadius: "50%" }}
          />
          FashionFlick
        </Navbar.Brand>

        <Navbar.Toggle aria-controls="FashionFlick-navbar" />
        <Navbar.Collapse id="FashionFlick-navbar">
          {/* Navigation Links */}
          <Nav className="ms-auto">
            {["home", "products", "about", "contact", "cart"].map((link) => (
              <Nav.Link
                key={link}
                href={link === "cart" ? "/cart" : `#${link}`} // still href for SEO, but main action is in onClick
                className={activeLink === link ? "active" : ""}
                onClick={(e) => {
                  e.preventDefault(); // prevent default anchor jump
                  handleLinkClick(link);
                }}
              >
                {link === "cart" ? "ðŸ›’ Cart" : link.charAt(0).toUpperCase() + link.slice(1)}
              </Nav.Link>
            ))}
          </Nav>

          {/* Search Form */}
          <Form className="d-flex align-items-center ms-3" onSubmit={handleSearch}>
            <FormControl
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
              size="sm"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Button variant="outline-light" size="sm" type="submit" className="m-1">
              Search
            </Button>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
