import React from "react";
import { Container, Row, Col, Card, Button, ListGroup } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Account = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear auth tokens or session here
    localStorage.removeItem("user");
    navigate("/");
  };

  return (
    <Container className="mt-5">
      <Row>
        {/* Sidebar */}
        <Col md={4}>
          <Card className="mb-4 shadow-sm">
            <Card.Header><strong>My Account</strong></Card.Header>
            <ListGroup variant="flush">
              <ListGroup.Item action onClick={() => navigate("/account/profile")}>👤 Profile</ListGroup.Item>
              <ListGroup.Item action onClick={() => navigate("/account/orders")}>📦 Orders</ListGroup.Item>
              <ListGroup.Item action onClick={() => navigate("/account/wishlist")}>❤️ Wishlist</ListGroup.Item>
              <ListGroup.Item action onClick={() => navigate("/account/address")}>🏠 Address</ListGroup.Item>
              <ListGroup.Item action onClick={handleLogout} className="text-danger">🚪 Logout</ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>

        {/* Main Content */}
        <Col md={8}>
          <Card className="shadow-sm">
            <Card.Header><strong>Welcome Back, Gokul!</strong></Card.Header>
            <Card.Body>
              <h5>Your Profile Overview</h5>
              <p><strong>Email:</strong> gokul@example.com</p>
              <p><strong>Phone:</strong> +91-9876543210</p>
              <p><strong>Member Since:</strong> Jan 2024</p>

              <Button variant="primary" className="me-2" onClick={() => navigate("/account/edit")}>Edit Profile</Button>
              <Button variant="outline-secondary" onClick={() => navigate("/account/orders")}>View Orders</Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Account;
